import React, { useState, useEffect } from 'react';
import { Music, BarChart3, Database, TrendingUp, Disc } from 'lucide-react';
import { Toaster, toast } from 'react-hot-toast';
import SearchBar from './components/SearchBar';
import SongTable from './components/SongTable';
import Pagination from './components/Pagination';
import CSVDownload from './components/CSVDownload';
import Charts from './components/Charts';
import { songsAPI } from './services/api';
import './App.css';

function App() {
  const [songs, setSongs] = useState([]);
  const [allSongs, setAllSongs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState(null);
  const [activeTab, setActiveTab] = useState('table');
  
  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [totalItems, setTotalItems] = useState(0);
  const [perPage] = useState(10);
  
  // Sorting state
  const [sortBy, setSortBy] = useState('index');
  const [sortOrder, setSortOrder] = useState('asc');

  // Fetch songs
  const fetchSongs = async (page = 1, sort = sortBy, order = sortOrder) => {
    try {
      setLoading(true);
      const response = await songsAPI.getAllSongs({
        page,
        per_page: perPage,
        sort_by: sort,
        order,
      });

      if (response.data.status === 'success') {
        setSongs(response.data.data);
        setCurrentPage(response.data.pagination.page);
        setTotalPages(response.data.pagination.total_pages);
        setTotalItems(response.data.pagination.total_items);
      }
    } catch (error) {
      console.error('Error fetching songs:', error);
      toast.error('Failed to load songs');
    } finally {
      setLoading(false);
    }
  };

  // Fetch all songs for CSV and charts
  const fetchAllSongs = async () => {
    try {
      const response = await songsAPI.getAllSongs({
        page: 1,
        per_page: 100,
        sort_by: 'index',
        order: 'asc',
      });

      if (response.data.status === 'success') {
        setAllSongs(response.data.data);
      }
    } catch (error) {
      console.error('Error fetching all songs:', error);
    }
  };

  // Fetch stats
  const fetchStats = async () => {
    try {
      const response = await songsAPI.getStats();
      if (response.data.status === 'success') {
        setStats(response.data.data);
      }
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  useEffect(() => {
    fetchSongs();
    fetchAllSongs();
    fetchStats();
  }, []);

  // Handle search
  const handleSearch = async (title) => {
    try {
      setLoading(true);
      const response = await songsAPI.searchSongs(title, false);

      if (response.data.status === 'success') {
        setSongs(response.data.data);
        setTotalItems(response.data.count || 0);
        setTotalPages(1);
        setCurrentPage(1);
        
        if (response.data.count === 0) {
          toast.error('No songs found');
        } else {
          toast.success(`Found ${response.data.count} song(s)`);
        }
      }
    } catch (error) {
      console.error('Error searching:', error);
      toast.error('Search failed');
    } finally {
      setLoading(false);
    }
  };

  // Handle clear search
  const handleClearSearch = () => {
    fetchSongs(1, sortBy, sortOrder);
  };

  // Handle sort
  const handleSort = (column) => {
    const newOrder = sortBy === column && sortOrder === 'asc' ? 'desc' : 'asc';
    setSortBy(column);
    setSortOrder(newOrder);
    fetchSongs(currentPage, column, newOrder);
  };

  // Handle page change
  const handlePageChange = (page) => {
    fetchSongs(page, sortBy, sortOrder);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // Handle rating update
  const handleRatingUpdate = async (songId, rating) => {
    try {
      const response = await songsAPI.updateRating(songId, rating);
      
      if (response.data.status === 'success') {
        // Update local state
        setSongs(prevSongs =>
          prevSongs.map(song =>
            song.id === songId ? { ...song, star_rating: rating } : song
          )
        );
        
        setAllSongs(prevSongs =>
          prevSongs.map(song =>
            song.id === songId ? { ...song, star_rating: rating } : song
          )
        );
        
        toast.success('Rating updated!', {
          icon: '⭐',
          duration: 2000,
        });
      }
    } catch (error) {
      console.error('Error updating rating:', error);
      toast.error('Failed to update rating');
    }
  };

  return (
    <div className="App">
      <Toaster
        position="top-right"
        toastOptions={{
          style: {
            background: 'rgba(40, 40, 40, 0.95)',
            color: '#fff',
            border: '1px solid rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(20px)',
          },
          success: {
            iconTheme: {
              primary: '#1DB954',
              secondary: '#fff',
            },
          },
          error: {
            iconTheme: {
              primary: '#ff6b6b',
              secondary: '#fff',
            },
          },
        }}
      />

      {/* Animated Background */}
      <div className="gradient-bg"></div>
      <div className="floating-orb orb-1"></div>
      <div className="floating-orb orb-2"></div>
      <div className="floating-orb orb-3"></div>

      {/* Header */}
      <header className="app-header">
        <h1 className="app-title">
          <Disc className="inline-block" size={60} />
          Playlist Dashboard
        </h1>
        <p className="app-subtitle">Your ultimate music collection analyzer</p>
      </header>

      {/* Stats Cards */}
      {stats && (
        <div className="stats-container">
          <div className="stat-card">
            <div className="stat-label">
              <Music size={16} className="inline" /> Total Songs
            </div>
            <div className="stat-value">{stats.total_songs}</div>
          </div>
          <div className="stat-card">
            <div className="stat-label">
              <TrendingUp size={16} className="inline" /> Avg Danceability
            </div>
            <div className="stat-value">{stats.average_danceability.toFixed(2)}</div>
          </div>
          <div className="stat-card">
            <div className="stat-label">
              <BarChart3 size={16} className="inline" /> Avg Energy
            </div>
            <div className="stat-value">{stats.average_energy.toFixed(2)}</div>
          </div>
          <div className="stat-card">
            <div className="stat-label">
              <Database size={16} className="inline" /> Avg Duration
            </div>
            <div className="stat-value">
              {Math.floor(stats.average_duration_ms / 60000)}:
              {Math.floor((stats.average_duration_ms % 60000) / 1000)
                .toString()
                .padStart(2, '0')}
            </div>
          </div>
        </div>
      )}

      {/* Main Content */}
      <div className="main-content">
        {/* Tabs */}
        <div className="tabs-container">
          <button
            className={`tab-button ${activeTab === 'table' ? 'active' : ''}`}
            onClick={() => setActiveTab('table')}
          >
            <Database size={20} />
            Data Table
          </button>
          <button
            className={`tab-button ${activeTab === 'charts' ? 'active' : ''}`}
            onClick={() => setActiveTab('charts')}
          >
            <BarChart3 size={20} />
            Analytics
          </button>
        </div>

        {/* Table Tab */}
        {activeTab === 'table' && (
          <>
            {/* Search Bar */}
            <SearchBar onSearch={handleSearch} onClear={handleClearSearch} />

            {/* Table Header */}
            <div className="table-container glass-card">
              <div className="table-header">
                <h2 className="table-title">
                  <Music size={28} />
                  Your Playlist
                </h2>
                <CSVDownload data={allSongs} filename="playlist_songs.csv" />
              </div>

              {/* Loading State */}
              {loading ? (
                <div className="loading-container">
                  <div className="spinner"></div>
                  <p className="loading-text">Loading your music...</p>
                </div>
              ) : (
                <>
                  {/* Song Table */}
                  <SongTable
                    songs={songs}
                    onSort={handleSort}
                    sortBy={sortBy}
                    sortOrder={sortOrder}
                    onRateUpdate={handleRatingUpdate}
                  />

                  {/* Pagination */}
                  {totalPages > 1 && (
                    <Pagination
                      currentPage={currentPage}
                      totalPages={totalPages}
                      totalItems={totalItems}
                      perPage={perPage}
                      onPageChange={handlePageChange}
                    />
                  )}
                </>
              )}
            </div>
          </>
        )}

        {/* Charts Tab */}
        {activeTab === 'charts' && (
          <div className="charts-section">
            {allSongs.length > 0 ? (
              <Charts songs={allSongs} />
            ) : (
              <div className="loading-container">
                <div className="spinner"></div>
                <p className="loading-text">Loading charts...</p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
